import React from 'react';
import { Link } from 'react-router-dom';
import { animateScroll as scroll } from 'react-scroll';

export const Home = () => {
  const scrollToSection = (sectionId) => {
    scroll.scrollTo(sectionId, {
      duration: 800, // тривалість анімації у мілісекундах
      smooth: 'easeInOutQuart', // тип анімації (можете експериментувати з типами)
    });
  };

  return (
    <div>
      <h1>Home Page</h1>
      <button onClick={() => scrollToSection('about-us-section')}>
        Go to About Us
      </button>
      <button onClick={() => scrollToSection('gallery-section')}>
        Go to Gallery
      </button>

      {/* ... ваш вміст ... */}
      <div id="about-us-section">
        {/* Вміст для секції "About Us" */}
      </div>

      <div id="gallery-section">
        {/* Вміст для секції "Gallery" */}
      </div>
    </div>
  );
};
