import React from 'react';
import { useHistory } from 'react-router-dom';
import { Course } from "./components/Course";
import { Gallery } from "./components/Gallery";
// import { Mentors } from "./components/Mentors";
// import { Partners } from "./components/Partners";
// import { AboutUs } from "./components/AboutUs";
import '../Slider';


export const Home = () => {

    const history = useHistory();

    const goToSlide = (slideNumber) => {
        // Переходимо на відповідну сторінку (слайд) за допомогою роутінгу
        history.push(`../Slider${slideNumber}`);
    };
      
    return (
        <div>
            <div>
                <button>Course</button>
                <button>Gallery</button>
                <button>Mentors</button>
                <button>Partners</button>
                <button>AboutUs</button>
            </div>

            <div>
                <button onClick={() => goToSlide(Course)}>Go to Slide 1</button>
                <button onClick={() => goToSlide(Gallery)}>Go to Slide 2</button>
            </div>
            <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Sint enim iste tenetur nam dolorem, accusamus ratione numquam facilis sequi minima fuga possimus eius sit earum, nihil magni? Voluptate, perspiciatis soluta.</p>
        </div>
    )
}
