import { Swiper, SwiperSlide } from "swiper/react";
import 'swiper/css';
import 'swiper/css/pagination';
import { Pagination } from 'swiper/modules';


export const Slider = ({slides}) => {
    // return (
    //     <Swiper
    //         const sections=["Home", "Course", "Gallery", "Mentors", "Partners", "About Us"];

    //         spaceBetween={50}
    //         slidesPerView={1}
    //         mousewheel={true}

    //         height={600}
        
