import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

import PartScrol1 from '../assets/images/PartScrol1.png';
import PartScrol2 from '../assets/images/PartScrol2.png';
import PartScrol3 from '../assets/images/PartScrol3.png';
import PartScrol4 from '../assets/images/PartScrol4.png';
import PartScrol5 from '../assets/images/PartScrol5.png';



export const Partners = () => {
  const settings = {
    infinite: true,
    speed: 500,
    slidesToShow: 3, // Кількість видимих елементів одночасно
    slidesToScroll: 1, // Кількість елементів, що прокручуються за раз
    autoplay: true, // Автоматична прокрутка
    autoplaySpeed: 2000, // Швидкість автоматичної прокрутки (2 секунди)
  };
  
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  return (
    <div>
      <h1>PARTNERS</h1>
      <Slider {...settings}>
        <div className={`carousel-item ${isHovered ? 'enlarged' : ''}`}>
          <img 
            src={PartScrol1} 
            alt="Зображення 1" 
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          />
        </div>
        <div className={`carousel-item ${isHovered ? 'enlarged' : ''}`}>
          <img 
            src={PartScrol1} 
            alt="Зображення 2" 
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          />
        </div>
        <div className={`carousel-item ${isHovered ? 'enlarged' : ''}`}>
          <img 
            src={PartScrol1} 
            alt="Зображення 3" 
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          />
        </div>
        <div className={`carousel-item ${isHovered ? 'enlarged' : ''}`}>
          <img 
            src={PartScrol1} 
            alt="Зображення 4" 
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          />
        </div>
        <div className={`carousel-item ${isHovered ? 'enlarged' : ''}`}>
          <img 
            src={PartScrol1} 
            alt="Зображення 5" 
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          />
        </div>




        {/* <div className="carousel-item">
          <img src={PartScrol1} alt="Зображення 1" />
        </div>
        <div className="carousel-item">
          <img src={PartScrol2} alt="Зображення 1" />
        </div>
        <div className="carousel-item">
          <img src={PartScrol3} alt="Зображення 1" />
        </div>
        <div className="carousel-item">
          <img src={PartScrol4} alt="Зображення 1" />
        </div>
        <div className="carousel-item">
          <img src={PartScrol5} alt="Зображення 1" />
        </div> */}


        
        {/* <div className="carousel-item">item 1</div>
        <div className="carousel-item">item 2</div>
        <div className="carousel-item">item 3</div>
        <div className="carousel-item">item 4</div>
        <div className="carousel-item">item 5</div> */}
      </Slider>
      <p>
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Sint enim iste
        tenetur nam dolorem, accusamus ratione numquam facilis sequi minima fuga
        possimus eius sit earum, nihil magni? Voluptate, perspiciatis soluta.
      </p>
    </div>
  );
};

