    }
];

export const Course = () => {
    const settings = {
      infinite: true,
      speed: 500,
      slidesToShow: 3,
      slidesToScroll: 1,
      autoplay: true,
      autoplaySpeed: 3000,
    };
  
    return (
      <div className='mentors'>
        <h1 className='titel'>MENTORS</h1>
        <Slider {...settings}>
          {courseData.map((mentor, index) => (
            <div className='container'>
              <div className='card' key={index}>
                  <div className='imgBX'>
                      <img src={mentor.image} alt={`Зображення ${index + 1}`} />
                  </div>
                  <div className='content'>
                      <h2>{mentor.title}</h2>
                      <p>{mentor.text}</p>
                  </div>
              </div>
            </div>
          ))}
        </Slider>
      </div>
    );
  };
